import Foundationimport FBSDKCoreKit/// Facebook Analytics helper for tracking conversion events/// This enables attribution of Facebook ads to actual subscription conversionsclass FacebookAnalytics {        /// Log when user views the paywall (conversion funnel start)    static func logPaywallViewed() {        AppEvents.shared.logEvent(AppEvents.Name("paywall_viewed"))        print("ğŸ“Š FB: Paywall viewed")    }        /// Log when user initiates checkout process    static func logCheckoutInitiated() {        AppEvents.shared.logEvent(AppEvents.Name("checkout_initiated"))        print("ğŸ“Š FB: Checkout initiated")    }        /// Log when user starts free trial    static func logTrialStarted(price: Double = 0.0, currency: String = "USD") {        AppEvents.shared.logEvent(AppEvents.Name("trial_started"), valueToSum: price)        print("ğŸ“Š FB: Trial started - \(currency)\(price)")    }        // âŒ REMOVED: logSubscriptionPurchase method    // RevenueCat handles purchase events server-side via Conversions API    // Client-side purchase logging would create duplicates and corrupt attribution data        /// Log when user completes onboarding (engagement signal)    static func logOnboardingCompleted() {        AppEvents.shared.logEvent(AppEvents.Name("onboarding_completed"))        print("ğŸ“Š FB: Onboarding completed")    }        /// Log when user creates their first memory (engagement signal)    static func logFirstMemoryCreated() {        AppEvents.shared.logEvent(AppEvents.Name("first_memory_created"))        print("ğŸ“Š FB: First memory created")    }} 