import Foundationimport SwiftUIstruct CoverSettings: Codable {    var title: String    var subtitle: String    var accentHex: String // hex color string    var coverPhotoData: Data?    var accentColor: Color {        Color(hex: accentHex) ?? .black    }}extension Color {    init?(hex: String) {        var hexStr = hex        if hexStr.hasPrefix("#") { hexStr.removeFirst() }        guard let int = UInt64(hexStr, radix: 16) else { return nil }        let r = Double((int >> 16) & 0xFF) / 255.0        let g = Double((int >> 8) & 0xFF) / 255.0        let b = Double(int & 0xFF) / 255.0        self.init(red: r, green: g, blue: b)    }    func toHex() -> String {        let ui = UIColor(self)        var r:CGFloat=0,g:CGFloat=0,b:CGFloat=0,a:CGFloat=0        ui.getRed(&r, green: &g, blue: &b, alpha: &a)        let iR = Int(r*255), iG = Int(g*255), iB = Int(b*255)        return String(format: "%02X%02X%02X", iR, iG, iB)    }} 