import Foundationextension String {    /// Splits the string into pages of roughly `wordsPerPage` words each.    func paginated(wordsPerPage: Int = 130) -> [String] {        let words = self.split { $0.isWhitespace }        guard words.count > wordsPerPage else {            return [self]        }        var pages: [String] = []        var start = 0        while start < words.count {            let end = min(start + wordsPerPage, words.count)            let slice = words[start..<end]            pages.append(slice.joined(separator: " "))            start += wordsPerPage        }        return pages    }}